import React, { Component } from 'react';
class Counter extends Component {
    state = {
        count: 1,
        tags: [],
    };
    constructor(){
        super();
        this.handleIncrement = this.handleIncrement.bind(this); // returns a new function
    }
    /*styles = {
        fontSize: 15,
        fontWeight: "bold"
    }; */

    renderTags(){
        if (this.state.tags.length === 0) return <p>There are no tags!</p>;
        return <ul> { this.state.tags.map(tag => <li key={tag}>{ tag }</li>)} </ul>
    }

    handleIncrement = () => {
        console.log('Increment Clicked', this);
    }
    render() {
        return (
            <React.Fragment>
                <span className= {this.getBadgeClasses()}>{this.formatCount()}</span>
                <button onClick={this.handleIncrement} className="btn btn-secondary btn-sm">Increment</button> <br></br>
                {this.state.tags.length === 0 && "Please create a new tag!"}
                {this.renderTags()}
            </React.Fragment> );
        // jsx expression which gets compiled to react.CreateELement
    }
    getBadgeClasses() {
        let classes = "badge m-2 badge-";
        classes += (this.state.count === 0) ? "warning" : "primary";
        return classes;
    }

    formatCount(){
        const { count } = this.state;
        return count === 0? 'Zero' :  count;
    }
}
  
export default Counter;
